void Window_Close(){
    SDL_GL_DeleteContext(context);
    SDL_DestroyWindow(window);
    SDL_Quit();
}

int Window_GetTicks(){
    return SDL_GetTicks();
}

void Window_Swap(){

    SDL_GL_SwapWindow(window);
}

void Window_PollEvent(void (*callback)(SDL_Event ev)){

    SDL_Event ev;

    while(SDL_PollEvent(&ev))
        callback(ev);
}

